import React, { useState, useRef } from 'react';
import './SongCard.css';

const songs = [
  { title: 'Song One', src: '/songs/song1.mp3' },
  { title: 'Song Two', src: '/songs/song2.mp3' },
  { title: 'Song Three', src: '/songs/song3.mp3' }
];

const SongCard = () => {
  const [current, setCurrent] = useState(0);
  const [playing, setPlaying] = useState(false);
  const audioRef = useRef(null);

  const togglePlay = () => {
    if (playing) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setPlaying(!playing);
  };

  const nextSong = () => {
    const next = (current + 1) % songs.length;
    setCurrent(next);
    setPlaying(false);
    setTimeout(() => {
      audioRef.current.play();
      setPlaying(true);
    }, 100);
  };

  const prevSong = () => {
    const prev = (current - 1 + songs.length) % songs.length;
    setCurrent(prev);
    setPlaying(false);
    setTimeout(() => {
      audioRef.current.play();
      setPlaying(true);
    }, 100);
  };

  return (
    <div className="song-card">
      <h2>{songs[current].title}</h2>
      <audio ref={audioRef} src={songs[current].src} />
      <div className="controls">
        <button onClick={prevSong}>⏮️ Prev</button>
        <button onClick={togglePlay}>{playing ? '⏸️ Pause' : '▶️ Play'}</button>
        <button onClick={nextSong}>⏭️ Next</button>
      </div>
    </div>
  );
};

export default SongCard;
