// src/App.js
import React, { useState, useRef } from 'react';
import './App.css';
import SongCard from './components/SongCard';

const songs = [
  { title: "Song One", url: "/music/song1.mp3" },
  { title: "Song Two", url: "/music/song2.mp3" },
  { title: "Song Three", url: "/music/song3.mp3" },
];
const playlist = [
  {
    title: "Sample Song",
    url: "/music/sample-song.mp3"
  }
];
function App() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const audioRef = useRef(null);

  const handlePlay = () => {
    audioRef.current.play();
  };

  const handleNext = () => {
    const next = (currentIndex + 1) % songs.length;
    setCurrentIndex(next);
  };

  const handlePrevious = () => {
    const prev = (currentIndex - 1 + songs.length) % songs.length;
    setCurrentIndex(prev);
  };

  return (
    <div className="App">
      <h1>Music Playlist</h1>
      <SongCard title={songs[currentIndex].title} />
      <audio ref={audioRef} src={songs[currentIndex].url} controls />
      <div>
        <button onClick={handlePrevious}>⏮️ Previous</button>
        <button onClick={handlePlay}>▶️ Play</button>
        <button onClick={handleNext}>⏭️ Next</button>
      </div>
    </div>
  );
}

export default App;
